function handleOrientation(e){var t=e.gamma.toFixed(1),a=e.beta.toFixed(1);og.innerHTML="x: "+t+"\n",ob.innerHTML="y : "+a+"\n",a>90&&(a=90),a<-90&&(a=-90),a+=90,t+=90;var n,r=Math.abs(t),o=Math.abs(a);n=r<o?o:r;var l=1-n/50;l=l.toFixed(2),target.style.borderColor=n<1?ballGreen:"#888",debug.innerHTML=n,debugOpacity.innerHTML=l,ball.style.transform="translate("+t+"px, "+a+"px)",ball.style.opacity=l}var level=document.querySelector(".level"),target=document.querySelector(".target"),ball=document.querySelector(".marker"),ballGreen="#a2fa66",ob=document.querySelector(".beta"),og=document.querySelector(".gamma"),debug=document.getElementById("debug"),debugOpacity=document.getElementById("debug-opacity");window.addEventListener("deviceorientation",handleOrientation);