function handleOrientation(e){var t=e.gamma.toFixed(1),n=e.beta.toFixed(1);og.innerHTML="x: "+t+"\n",ob.innerHTML="y : "+n+"\n",n>90&&(n=90),n<-90&&(n=-90),n+=90,t+=90,ball.style.transform="translate("+t+"px, "+n+"px)";var a,r=Math.abs(t),o=Math.abs(n);a=r<o?o:r,debug.innerHTML=a}var level=document.querySelector(".level"),ball=document.querySelector(".marker"),ob=document.querySelector(".beta"),og=document.querySelector(".gamma"),debug=document.getElementById("debug");window.addEventListener("deviceorientation",handleOrientation);